mapper.select:
		select id,user_id,username,operation,time,method,params,ip,gmt_create from sys_log

		  		   and id = #{id} 
		  		   and user_id = #{userId} 
		  		   and username = #{username} 
		  		   and operation = #{operation} 
		  		   and time = #{time} 
		  		   and method = #{method} 
		  		   and params = #{params} 
		  		   and ip = #{ip} 
		  		   and gmt_create = #{gmtCreate} 



                order by ${sort} ${order}


                order by id desc

select count(*) from 

  		   and id = #{id} 
  		   and user_id = #{userId} 
  		   and username = #{username} 
  		   and operation = #{operation} 
  		   and time = #{time} 
  		   and method = #{method} 
  		   and params = #{params} 
  		   and ip = #{ip} 
  		   and gmt_create = #{gmtCreate}

mapper.update:
update
user_id = #{userId}, username = #{username}, operation = #{operation}, time = #{time}, method = #{method}, params = #{params}, ip = #{ip}, gmt_create = #{gmtCreate}
where id = #{id}
